(()=>{function d(){let t=document.getElementById("blog-stats"),n=document.getElementById("running-days");if(!t||!n)return;let i=t.getAttribute("data-launch-date");if(!i){n.textContent="0";return}try{let a=new Date(i+"T00:00:00Z"),o=new Date,c=new Date(Date.UTC(o.getFullYear(),o.getMonth(),o.getDate())).getTime()-a.getTime(),s=Math.max(0,Math.floor(c/(1e3*60*60*24)));n.textContent=s.toString()}catch(a){console.error("\u8BA1\u7B97\u8FD0\u884C\u5929\u6570\u65F6\u51FA\u9519:",a),n.textContent="0"}}function e(){d()}document.readyState==="loading"?document.addEventListener("DOMContentLoaded",e):e();window.addEventListener("load",()=>{if(e(),window.Stack&&window.Stack.init){let t=window.Stack.init;window.Stack.init=function(){t.apply(this,arguments),setTimeout(e,100)}}});window.updateRunningDays=d;})();
